import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Variouslessons() {
  const { id } = useParams();
  const [signs, setSigns] = useState([]);

  useEffect(() => {
    fetch(`/api/signs/lesson/${id}`) // proxy
      .then((res) => res.json())
      .then((data) => setSigns(data))
      .catch((err) => console.error("Error:", err));
  }, [id]);

  if (!lesson) return <p className="p-6">กำลังโหลด...</p>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold">
        บทเรียนที่ {id}: {/* {lesson.title} */}
      </h2>
      <p className="mt-2 text-gray-700">{lesson.description}</p>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        {signs.length > 0 ? (
          signs.map((sign) => (
            <div
              key={sign.sign_id}
              className="border rounded-lg p-4 shadow hover:shadow-lg transition"
            >
              <h3 className="font-semibold text-lg">{sign.word}</h3>
              <p className="text-gray-600">{sign.meaning}</p>
              {sign.image_url && (
                <img
                  src={`/${sign.image_url}`}
                  alt={sign.word}
                  className="mt-2 w-full h-40 object-contain"
                />
              )}
              {sign.video_url && (
                <video
                  controls
                  className="mt-2 w-full rounded-lg"
                  src={`/${sign.video_url}`}
                />
              )}
            </div>
          ))
        ) : (
          <p className="col-span-3 text-gray-500">ยังไม่มีสัญลักษณ์ในบทนี้</p>
        )}
      </div>
    </div>
  );
}

export default Variouslessons;
