import { useEffect, useState } from "react";

function Lessons() {
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="relative w-full min-h-screen bg-[url('/your-background.jpg')] bg-cover bg-center">
      {/* Overlay ดำครึ่งโปร่ง */}
      <div className="absolute inset-0 bg-black/50"></div>

      {/* Content */}
      <div className="relative z-10 px-6 py-12 max-w-6xl mx-auto text-white">
        <h1 className="text-4xl md:text-5xl font-bold text-center">
          บทเรียนทั้งหมด
        </h1>
        <p className="text-gray-300 text-center mt-2">
          มีทั้งหมด {lessons.length} บทเรียน
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-12">
          {lessons.map((lesson, index) => (
            <div
              key={index}
              className="flex items-start bg-white/10 backdrop-blur-md p-6 rounded-lg shadow-sm hover:shadow-md transition"
            >
              {/* Circle Number */}
              <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full text-lg font-bold">
                {lesson.id || index + 1}
              </div>

              {/* Lesson Content */}
              <div className="ml-4">
                <h3 className="font-semibold text-lg">
                  บทที่ {lesson.id || index + 1}: {lesson.title}
                </h3>
                <p className="text-gray-200 text-sm mt-1">{lesson.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Lessons;
