import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function AddLesson() {
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [lesson, setLesson] = useState({ title: "", description: "" });

  const handleChange = (e) => {
    setLesson({ ...lesson, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:3000/lessons", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(lesson),
      });

      if (res.ok) {
        MySwal.fire("สำเร็จ!", "เพิ่มบทเรียนเรียบร้อยแล้ว", "success");
        navigate("/dashboard");
      } else {
        MySwal.fire("ผิดพลาด!", "ไม่สามารถเพิ่มบทเรียนได้", "error");
      }
    } catch (err) {
      console.error(err);
      MySwal.fire("ผิดพลาด!", "เกิดข้อผิดพลาดในการเชื่อมต่อ", "error");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-lg rounded-lg p-6">
      <h1 className="text-2xl font-semibold mb-6 text-gray-800">เพิ่มบทเรียน</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 text-gray-700">ชื่อบทเรียน</label>
          <input
            type="text"
            name="title"
            value={lesson.title}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>
        <div>
          <label className="block mb-1 text-gray-700">คำอธิบาย</label>
          <textarea
            name="description"
            value={lesson.description}
            onChange={handleChange}
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          ></textarea>
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
