import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function AddExercise() {
  const navigate = useNavigate();
  const [lessonId, setLessonId] = useState("");
  const [question, setQuestion] = useState("");
  const [optionA, setOptionA] = useState("");
  const [optionB, setOptionB] = useState("");
  const [optionC, setOptionC] = useState("");
  const [optionD, setOptionD] = useState("");
  const [correctAnswer, setCorrectAnswer] = useState("");
  const [image, setImage] = useState(null);
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then(res => res.json())
      .then(data => setLessons(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("lesson_id", lessonId);
    formData.append("question", question);
    formData.append("option_a", optionA);
    formData.append("option_b", optionB);
    formData.append("option_c", optionC);
    formData.append("option_d", optionD);
    formData.append("correct_answer", correctAnswer);
    if (image) formData.append("image_data", image);

    try {
      const res = await fetch("http://localhost:3000/exercises", {
        method: "POST",
        body: formData,
      });
      if (res.ok) {
        alert("Exercise added successfully");
        navigate("/dashboard");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to add exercise");
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Add Exercise</h1>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-md">
        <select value={lessonId} onChange={(e) => setLessonId(e.target.value)} required>
          <option value="">Select Lesson</option>
          {lessons.map(l => (
            <option key={l.lesson_id} value={l.lesson_id}>{l.lesson_title}</option>
          ))}
        </select>
        <textarea
          placeholder="Question"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          required
        />
        <input type="text" placeholder="Option A" value={optionA} onChange={(e) => setOptionA(e.target.value)} />
        <input type="text" placeholder="Option B" value={optionB} onChange={(e) => setOptionB(e.target.value)} />
        <input type="text" placeholder="Option C" value={optionC} onChange={(e) => setOptionC(e.target.value)} />
        <input type="text" placeholder="Option D" value={optionD} onChange={(e) => setOptionD(e.target.value)} />
        <input type="text" placeholder="Correct Answer" value={correctAnswer} onChange={(e) => setCorrectAnswer(e.target.value)} />
        <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Save
        </button>
      </form>
    </div>
  );
}
