// src/user/Login.js
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:3000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (res.ok) {
        login({ user_id: data.user_id }, data.token);
        navigate("/exercises");
      } else {
        alert(data.message || data.error);
      }
    } catch (err) {
      console.error(err);
    }
  };

  // ฟังก์ชัน Login เป็น Guest
  const handleGuestLogin = async () => {
    try {
      const res = await fetch("http://localhost:3000/user/guest", {
        method: "POST",
      });
      const data = await res.json();
      if (res.ok) {
        login({ user_id: data.user_id }, data.token);
        navigate("../");
      } else {
        alert(data.message || data.error);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="container mx-auto p-6 max-w-md">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="p-2 border rounded"
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="p-2 border rounded"
          required
        />
        <button type="submit" className="bg-blue-600 text-white p-2 rounded">
          Login
        </button>
      </form>

      {/* ปุ่ม Login as Guest */}
      <button
        onClick={handleGuestLogin}
        className="mt-4 w-full bg-gray-500 text-white p-2 rounded"
      >
        Login as Guest
      </button>

      {/* ลิงก์ Register / Forgot Password */}
      <div className="flex justify-between mt-4 text-sm text-blue-600">
        <Link to="/register">สมัครสมาชิก</Link>
        <Link to="/forgot-password">ลืมรหัสผ่าน?</Link>
      </div>
    </div>
  );
}

export default Login;
