import { useEffect, useState } from "react";
import axios from "axios";

export default function Score() {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);

  // ดึงข้อมูล user จาก localStorage
  const username = localStorage.getItem("username");
  const userId = localStorage.getItem("userId");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!userId || !token) {
      setLoading(false);
      return;
    }

    const fetchScores = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/scores/me`, {
  headers: { Authorization: `Bearer ${token}` },
});

        setScores(res.data);
      } catch (err) {
        console.error("Error fetching scores:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [userId, token]);

  if (!userId) {
    return (
      <div className="flex items-center justify-center h-screen text-xl text-gray-600">
        กรุณาเข้าสู่ระบบก่อนดูคะแนน
      </div>
    );
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-xl text-gray-500">
        กำลังโหลดข้อมูล...
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto mt-10 bg-white p-6 rounded-2xl shadow">
      <h1 className="text-2xl font-bold mb-4 text-center text-blue-600">
        คะแนนของคุณ {username || "Guest"}
      </h1>

      {scores.length === 0 ? (
        <p className="text-center text-gray-500">ยังไม่มีคะแนนในระบบ</p>
      ) : (
        <table className="w-full border-collapse border border-gray-200">
          <thead>
            <tr className="bg-blue-100">
              <th className="border p-2">บทเรียน</th>
              <th className="border p-2">ข้อแบบฝึกหัด</th>
              <th className="border p-2">คะแนน</th>
              <th className="border p-2">วันที่ทำเสร็จ</th>
            </tr>
          </thead>
          <tbody>
            {scores.map((s) => (
              <tr key={s.id} className="text-center hover:bg-gray-50">
                <td className="border p-2">{s.lesson_number}</td>
                <td className="border p-2">{s.exercise_id}</td>
                <td className="border p-2 font-semibold text-green-600">{s.score}</td>
                <td className="border p-2 text-gray-500">
                  {new Date(s.completed_at).toLocaleString("th-TH")}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
