export default function ContentSection({ title, items, onEdit, onDelete, onAdd }) {
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const handleEdit = async (id) => {
    const result = await MySwal.fire({
      title: "คุณต้องการแก้ไขข้อมูลนี้หรือไม่?",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก",
    });

    if (!result.isConfirmed) return;

    if (title.includes("บทเรียน")) {
      navigate(`/edit-lesson/${id}`);
    } else if (title.includes("คำศัพท์")) {
      navigate(`/edit-word/${id}`);
    } else if (title.includes("คำถาม")) {
      navigate(`/edit-exercise/${id}`);
    } else if (title.includes("ผู้ดูแลระบบ")) {
      navigate(`/edit-admin/${id}`);
    } else {
      alert("ไม่พบหน้าสำหรับแก้ไขข้อมูลนี้");
    }
  };

  const handleDelete = async (id) => {
    const result = await MySwal.fire({
      title: "คุณแน่ใจหรือไม่?",
      text: "เมื่อกดตกลง ข้อมูลนี้จะถูกลบถาวร",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก",
    });

    if (result.isConfirmed) {
      onDelete(id);
      MySwal.fire("ลบเรียบร้อย!", "ข้อมูลถูกลบแล้ว", "success");
    }
  };

  const handleAddClick = () => {
    if (onAdd) {
      onAdd();
    } else {
      // fallback: navigate ตาม type
      if (title.includes("บทเรียน")) navigate("/add-lesson");
      else if (title.includes("คำศัพท์")) navigate("/add-sign");
      else if (title.includes("คำถาม")) navigate("/add-exercise");
      else if (title.includes("ผู้ดูแลระบบ")) navigate("/add-admin");
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-md p-5 w-full">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-semibold text-lg">{title}</h3>
        <button
          onClick={handleAddClick}
          className="bg-green-600 text-white px-2 py-1 text-sm rounded-md hover:bg-green-700"
        >
          + Add
        </button>
      </div>

      <div className="max-h-48 overflow-y-auto pr-2 scrollbar-thin scrollbar-thumb-gray-300 scrollbar-track-gray-100 rounded-md">
        {items.length === 0 ? (
          <p className="text-gray-400 text-sm">ไม่มีข้อมูล</p>
        ) : (
          <ul className="text-sm space-y-2">
            {items.map((item) => {
              const name = item.title || item.word || item.question || item.name || "N/A";
              const id =
                item.lesson_id || item.sign_id || item.exercise_id || item.admin_id;

              return (
                <li
                  key={id}
                  className="flex justify-between items-center border-b py-1"
                >
                  <span>{name}</span>
                  <div className="flex gap-2">
                    <Edit
                      className="w-4 h-4 cursor-pointer text-gray-500 hover:text-blue-500"
                      onClick={() => handleEdit(id)}
                    />
                    <Trash2
                      className="w-4 h-4 cursor-pointer text-gray-500 hover:text-red-500"
                      onClick={() => handleDelete(id)}
                    />
                  </div>
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
}
