import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function AddSign() {
  const navigate = useNavigate();
  const [lessonId, setLessonId] = useState("");
  const [word, setWord] = useState("");
  const [meaning, setMeaning] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [videoUrl, setVideoUrl] = useState("");
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    // ✅ ดึงรายการบทเรียนทั้งหมด (ไม่ต้องใช้ id)
    fetch("http://localhost:3000/")
      .then(res => res.json())
      .then(data => setLessons(data))
      .catch(err => console.error("Error fetching lessons:", err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!lessonId) {
      alert("กรุณาเลือกบทเรียนก่อนบันทึก");
      return;
    }

    const formData = new FormData();
    formData.append("lesson_id", lessonId);
    formData.append("word", word);
    formData.append("meaning", meaning);
    formData.append("description", description);
    formData.append("video_url", videoUrl);
    if (image) formData.append("image", image); // ✅ เปลี่ยนชื่อ field ให้ตรงกับ backend

    try {
      // ✅ เรียก API สร้าง sign ใหม่ในบทที่เลือก
      const res = await fetch(`http://localhost:3000/signs`, {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Failed to add sign");

      alert("เพิ่มคำศัพท์สำเร็จ");
      navigate("/dashboard");
    } catch (err) {
      console.error("Error adding sign:", err);
      alert("เกิดข้อผิดพลาดในการเพิ่มคำศัพท์");
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">เพิ่มคำศัพท์ใหม่</h1>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-md">
        <select
          value={lessonId}
          onChange={(e) => setLessonId(e.target.value)}
          required
        >
          <option value="">เลือกบทเรียน</option>
          {lessons.map((l) => (
            <option key={l.lesson_id} value={l.lesson_id}>
              {l.lesson_title}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="คำศัพท์ (ไทย)"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          required
        />

        <input
          type="text"
          placeholder="คำศัพท์ (อังกฤษ)"
          value={meaning}
          onChange={(e) => setMeaning(e.target.value)}
        />

        <textarea
          placeholder="คำอธิบาย"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />

        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />

        <input
          type="text"
          placeholder="Video URL"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
