import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Narbar";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Lessons from "./pages/Lessons";
import Exercise from "./pages/Exercise";
import Admin from "./pages/Admin";
import Variouslessons from "./pages/Variouslessons";
import Testexercise from "./pages/Testexercise";
import Dashboard from "./pages/Dashboard";
import EditLesson from "./pages/EditLesson";
import EditSign from "./pages/EditSign";
import EditExercise from "./pages/EditExercise";
import EditAdmin from "./pages/EditAdmin";
import AddLesson from "./pages/AddLesson";
import AddSign from "./pages/AddSign";
import AddExercise from "./pages/AddExercise";
import AddAdmin from "./pages/AddAdmin";

function App() {
  return (
    <Router>
      <Routes>
        {/* Admin Login Route แบบเต็มจอ ไม่มี Navbar/ Footer */}
        <Route path="/admin/admin" element={<Admin />} />
        <Route path="/dashboard" element={<Dashboard />} />


        {/* Routes ปกติที่มี Navbar + Footer */}
        <Route
          path="*"
          element={
            <div className="flex flex-col min-h-screen">
              <Navbar />
              <main className="flex-grow">
                <Routes>
                  <Route path="/" element={<Home />} />
                  <Route path="/lessons" element={<Lessons />} />
                  <Route path="/exercises" element={<Exercise />} />
                  <Route path="/lessons/:id" element={<Variouslessons />} />
                  <Route path="/exercises/lesson/:lessonNumber" element={<Testexercise />} />

                </Routes>
              </main>
              <Footer />
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
