// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Users, Barcode } from "lucide-react";
import Card from "../components-Dashboard/Card";
import ContentSection from "../components-Dashboard/ContentSection";
import AdminTable from "../components-Dashboard/AdminTable";
import Header from "../components-Dashboard/Header";
import Sidebar from "../components-Dashboard/Sidebar";

// AddPopup ‡πÄ‡∏õ‡πá‡∏ô component ‡∏õ‡∏Å‡∏ï‡∏¥ (‡πÑ‡∏°‡πà export ‡∏Ç‡πâ‡∏≤‡∏á‡πÉ‡∏ô‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏≠‡∏∑‡πà‡∏ô)
function AddPopup({ onSelect, onClose }) {
  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-xl shadow-xl relative w-80 text-center">
        {/* ‡∏õ‡∏∏‡πà‡∏° X */}
        <button
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-500 hover:text-red-500 text-xl font-bold"
          aria-label="Close"
        >
          √ó
        </button>

        <h2 className="text-xl font-semibold mb-5 text-gray-700">
          ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏û‡∏¥‡πà‡∏°
        </h2>

        <div className="flex flex-col gap-3">
          <button
            onClick={() => onSelect("lessons")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-lg transition"
          >
            ‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô (Lesson)
          </button>

          <button
            onClick={() => onSelect("signs")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 rounded-lg transition"
          >
            ‚úã ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå (Sign)
          </button>

          <button
            onClick={() => onSelect("exercises")}
            className="bg-yellow-500 hover:bg-yellow-600 text-white py-2 rounded-lg transition"
          >
            üß© ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏° (Exercise)
          </button>

          <button
            onClick={() => onSelect("admins")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 rounded-lg transition"
          >
            üë§ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•‡∏£‡∏∞‡∏ö‡∏ö (Admin)
          </button>
        </div>
      </div>
    </div>
  );
}

export default function Dashboard() {
  const navigate = useNavigate();
  const [lessons, setLessons] = useState([]);
  const [words, setWords] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // <-- state ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÇ‡∏ä‡∏ß‡πå popup

  useEffect(() => {
    fetchLessons();
    fetchWords();
    fetchQuestions();
    fetchAdmins();
  }, []);

  const fetchLessons = async () => {
    try {
      const res = await fetch("http://localhost:3000/lessons");
      const data = await res.json();
      setLessons(data);
    } catch (err) {
      console.error("Error fetching lessons:", err);
    }
  };

  const fetchWords = async () => {
    try {
      const res = await fetch("http://localhost:3000/signs");
      const data = await res.json();
      setWords(data);
    } catch (err) {
      console.error("Error fetching signs:", err);
    }
  };

  const fetchQuestions = async () => {
    try {
      const res = await fetch("http://localhost:3000/exercises");
      const data = await res.json();
      setQuestions(data);
    } catch (err) {
      console.error("Error fetching exercises:", err);
    }
  };

  const fetchAdmins = async () => {
    try {
      const res = await fetch("http://localhost:3000/admins");
      const data = await res.json();
      setAdmins(data);
    } catch (err) {
      console.error("Error fetching admins:", err);
    }
  };

  // ‡πÄ‡∏õ‡∏¥‡∏î popup
  const handleAddClick = () => {
    setShowPopup(true);
  };

  // ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó ‡πÉ‡∏´‡πâ‡∏õ‡∏¥‡∏î popup ‡πÅ‡∏•‡πâ‡∏ß navigate ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ add ‡∏ó‡∏µ‡πà‡πÄ‡∏´‡∏°‡∏≤‡∏∞‡∏™‡∏°
  const handleSelect = (type) => {
    setShowPopup(false);
    switch (type) {
      case "lessons":
        navigate("/add-lesson");
        break;
      case "signs":
        navigate("/add-sign");
        break;
      case "exercises":
        navigate("/add-exercise");
        break;
      case "admins":
        navigate("/add-admin");
        break;
      default:
        break;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar />

      <div className="flex-1 p-8">
        <Header />
        <h1 className="text-3xl font-bold text-gray-800 mb-8">üìä Dashboard</h1>

        {/* Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <Card title="Total Users" value="120" icon={Users} />
          <Card title="Content Items" value="53" icon={Barcode} />
        </div>

        {/* Content Management */}
        <section>
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Content Management</h2>
          <div className="flex justify-between items-center mb-6">
            <input
              type="text"
              placeholder="Search..."
              className="border border-gray-300 rounded-md px-3 py-2 text-sm w-1/2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              onClick={handleAddClick}
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              + Add
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <ContentSection
              title="‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô"
              items={lessons}
              onEdit={(lesson) => navigate(`/edit-lesson/${lesson.lesson_id}`)}
              onDelete={async (lesson) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, { method: "DELETE" });
                  fetchLessons();
                }
              }}
            />
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå"
              items={words}
              onEdit={(word) => navigate(`/edit-word/${word.word_id}`)}
              onDelete={async (word) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/signs/${word.word_id}`, { method: "DELETE" });
                  fetchWords();
                }
              }}
            />
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°"
              items={questions}
              onEdit={(q) => navigate(`/edit-exercise/${q.question_id}`)}
              onDelete={async (q) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/exercises/${q.question_id}`, { method: "DELETE" });
                  fetchQuestions();
                }
              }}
            />
          </div>
        </section>

        {/* Admin Table */}
        <section className="mt-10">
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Admin Management</h2>
          <AdminTable data={admins} />
