import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();
  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);

  // Fetch lesson
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [lessonNumber]);

  // Handle time up
  const handleTimeUp = useCallback(() => {
    if (!showResult && lesson) {
      const answer = selectedOption || "ไม่ได้เลือก";
      setUserAnswers((prev) => [...prev, answer]);
      if (answer === lesson.questions[currentQuestion].answer) {
        setScore((prev) => prev + 1);
      }
      setShowResult(true);
    }
  }, [selectedOption, currentQuestion, lesson, showResult]);

  // Countdown timer
  useEffect(() => {
    if (showResult) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleTimeUp();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [showResult, handleTimeUp]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const questions = lesson.questions;

  const handleNext = () => {
    const answer = selectedOption || "ไม่ได้เลือก";
    setUserAnswers([...userAnswers, answer]);
    if (answer === questions[currentQuestion].answer) {
      setScore(score + 1);
    }
    setSelectedOption("");
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowResult(true);
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
  };

  // Result Page
  if (showResult) {
    return (
      <div className="px-4 py-12 flex justify-center">
        <div className="w-full max-w-3xl text-center">
          <h1 className="text-3xl font-bold mb-4">{lesson.title} - ผลลัพธ์</h1>
          <p className="text-lg mb-2">คุณทำถูก {score} / {questions.length} ข้อ</p>
          <p className="text-gray-700 mb-6">เวลาที่ใช้: {formatTime(90 - timeLeft)}</p>

          <div className="flex flex-wrap justify-center gap-4 mb-6">
            <button
              className="px-4 py-2 bg-green-500 text-white rounded shadow hover:bg-green-600 transition"
              onClick={handleRestart}
            >
              ทำอีกครั้ง
            </button>
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded shadow hover:bg-blue-600 transition"
              onClick={() => setShowAnswers(true)}
            >
              เช็คข้อที่ถูก/ผิด
            </button>
            <button
              className="px-4 py-2 bg-gray-500 text-white rounded shadow hover:bg-gray-600 transition"
              onClick={() => navigate(-1)}
            >
              ย้อนกลับ
            </button>
          </div>

          {showAnswers && (
            <div className="mt-6 text-left">
              {questions.map((q, idx) => {
                const userAnswer = userAnswers[idx] || "ไม่ได้เลือก";
                const isCorrect = userAnswer === q.answer;
                return (
                  <div
                    key={idx}
                    className={`p-4 mb-3 rounded-lg border transition ${
                      isCorrect
                        ? "bg-green-100 border-green-500"
                        : "bg-red-100 border-red-500"
                    }`}
                  >
                    <p className="font-semibold">{idx + 1}. {q.question}</p>
                    <p className="mt-2">
                      คำตอบของคุณ:{" "}
                      <span className={`px-2 py-1 rounded ${isCorrect ? "bg-green-200 text-green-800" : "bg-red-200 text-red-800"}`}>
                        {userAnswer}
                      </span>
                    </p>
                    {!isCorrect && (
                      <p className="mt-1 text-green-700">คำตอบที่ถูกต้อง: {q.answer}</p>
                    )}
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    );
  }

  // Quiz Page
  const question = questions[currentQuestion];

  return (
    <div className="px-4 py-12 flex justify-center">
      <div className="w-full max-w-3xl">
        {/* Header */}
        <div className="mb-4 text-center">
          <h1 className="text-3xl font-bold">{lesson.title}</h1>
          <p className="text-gray-600 mt-1">
            ข้อ {currentQuestion + 1} / {questions.length}
          </p>

          {/* Progress Bar */}
          <div className="w-full bg-gray-200 h-2 rounded-full mt-2">
            <div
              className="h-2 rounded-full bg-green-500 transition-all duration-300"
              style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
            ></div>
          </div>

          {/* Timer */}
          <p className={`mt-2 font-semibold ${timeLeft <= 10 ? "text-red-500" : "text-green-700"}`}>
            เวลาเหลือ: {formatTime(timeLeft)}
          </p>
          <div className="w-full bg-red-100 h-2 rounded-full mt-1">
            <div
              className={`h-2 rounded-full transition-all duration-500 ${timeLeft <= 10 ? "bg-red-600" : "bg-green-500"}`}
              style={{ width: `${(timeLeft / 90) * 100}%` }}
            ></div>
          </div>
        </div>

        {/* Question Card */}
        <div className="bg-white p-6 rounded-lg shadow-lg transition hover:shadow-xl">
          <h3 className="text-lg font-semibold mb-4">{question.question}</h3>
          {question.image_data && (
            <div className="flex justify-center mb-4">
              <img
                src={`data:image/jpeg;base64,${question.image_data}`}
                alt="question"
                className="w-40 h-40 object-contain rounded"
              />
            </div>
          )}

          {/* Options */}
          <div className="flex flex-col gap-3">
            {question.options.map((opt, idx) => (
              <button
                key={idx}
                className={`p-3 rounded-lg border text-left transition 
                  ${selectedOption === opt ? "bg-blue-500 text-white shadow" : "bg-white hover:bg-blue-50"} 
                  ${timeLeft === 0 ? "opacity-50 cursor-not-allowed" : ""}`}
                onClick={() => timeLeft > 0 && setSelectedOption(opt)}
                disabled={timeLeft === 0}
              >
                {opt}
              </button>
            ))}
          </div>

          {/* Navigation */}
          <div className="flex justify-center gap-4 mt-6">
            <button
              className="px-6 py-2 bg-gray-400 text-white rounded shadow hover:bg-gray-500 disabled:opacity-50"
              onClick={() => {
                if (currentQuestion > 0) {
                  setCurrentQuestion(currentQuestion - 1);
                  setSelectedOption(userAnswers[currentQuestion - 1] || "");
                }
              }}
              disabled={currentQuestion === 0 || timeLeft === 0}
            >
              ย้อนกลับ
            </button>

            <button
              className="px-6 py-2 bg-green-500 text-white rounded shadow hover:bg-green-600 disabled:opacity-50"
              onClick={handleNext}
              disabled={!selectedOption || timeLeft === 0}
            >
              {currentQuestion + 1 < questions.length ? "ถัดไป" : "สรุปผล"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Testexercise;
