import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function EditLesson() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [lesson, setLesson] = useState({
    lesson_id: "", // เพิ่ม lesson_id
    lesson_number: "",
    lesson_title: "",
    description: "",
    image_data: null
  });

  // โหลดข้อมูลบทเรียน
  useEffect(() => {
    fetch(`http://localhost:3000/lessons/${id}`)
      .then(res => res.json())
      .then(data => {
        setLesson({
          lesson_id: data.lesson_id,
          lesson_number: data.lesson_number,
          lesson_title: data.lesson_title,
          description: data.description,
          image_data: data.image_data
        });
      })
      .catch(err => console.error(err));
  }, [id]);

  // ฟังก์ชัน handleChange ต้องอยู่ใน scope ของ component
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files.length > 0) {
      const reader = new FileReader();
      reader.onloadend = () => setLesson(prev => ({ ...prev, image_data: reader.result }));
      reader.readAsDataURL(files[0]);
    } else {
      setLesson(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!window.confirm("คุณแน่ใจว่าต้องการบันทึกการแก้ไข?")) return;

    try {
      const res = await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(lesson),
      });
      if (!res.ok) throw new Error("Update failed");
      alert("แก้ไขเรียบร้อยแล้ว");
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
      alert("เกิดข้อผิดพลาดในการแก้ไข");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขบทเรียน</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="lesson_number"
          value={lesson.lesson_number}
          onChange={handleChange} // ✅ ต้องเรียก handleChange ที่ประกาศด้านบน
          placeholder="Lesson Number"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="lesson_title"
          value={lesson.lesson_title}
          onChange={handleChange}
          placeholder="Title"
          className="w-full border px-3 py-2 rounded-md"
        />
        <textarea
          name="description"
          value={lesson.description}
          onChange={handleChange}
          placeholder="Description"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input type="file" name="image" onChange={handleChange} />
        {lesson.image_data && (
          <img
            src={lesson.image_data}
            alt="Lesson"
            className="mt-2 w-32 h-32 object-cover"
          />
        )}
        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded-md"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
