import React, { useEffect, useState, useRef } from "react";
import heroImage from "../assets/hero.webp";
import { Link } from "react-router-dom";
import { ChevronLeft, ChevronRight } from "lucide-react";

function LessonsScroll() {
  const [lessons, setLessons] = useState([]);
  const scrollRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => {
        const sorted = data.sort((a, b) => a.number - b.number);
        setLessons(sorted);
      })
      .catch((err) => console.error("Error:", err));
  }, []);

  const scroll = (direction) => {
    const container = scrollRef.current;
    const scrollAmount = 300;

    if (direction === "left") {
      container.scrollBy({ left: -scrollAmount, behavior: "smooth" });
    } else {
      container.scrollBy({ left: scrollAmount, behavior: "smooth" });
    }
  };

  return (
    <section className="py-20 px-6 bg-white relative">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-2xl md:text-3xl font-bold mb-6">‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</h2>
        <p className="text-gray-600 max-w-2xl mx-auto mb-12">
          ‡∏Å‡∏î‡∏•‡∏π‡∏Å‡∏®‡∏£‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÄ‡∏•‡∏∑‡πà‡∏≠‡∏ô‡∏î‡∏π‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏¢ üëá
        </p>

        {/* ‡∏õ‡∏∏‡πà‡∏°‡∏•‡∏π‡∏Å‡∏®‡∏£‡∏ã‡πâ‡∏≤‡∏¢ */}
        <button
          onClick={() => scroll("left")}
          className="absolute left- top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-3 rounded-full shadow-lg hover:bg-gray-700 z-10"
        >
          <ChevronLeft size={24} />
        </button>

        {/* Container ‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡πà‡∏≠‡∏ô‡∏î‡πâ‡∏ß‡∏¢ ref */}
        <div
          ref={scrollRef}
          className="flex gap-6 overflow-x-auto pb-4 scrollbar-hide scroll-smooth"
        >
          {lessons.length > 0 ? (
            lessons.map((lesson) => (
              <div
                key={lesson.number}
                className="min-w-[280px] bg-white rounded-lg shadow-md hover:shadow-lg transition-transform hover:scale-105 overflow-hidden"
              >
                <img
                  src={lesson.image_data || "placeholder.jpg"}
                  alt={lesson.title}
                  className="w-full h-48 object-cover"
                />
                <div className="p-6 text-left">
                  <h3 className="font-semibold text-lg">
                    ‡∏ö‡∏ó‡∏ó‡∏µ‡πà {lesson.number}: {lesson.title}
                  </h3>
                  <p className="text-gray-600 text-sm mt-2">
                    {lesson.description}
                  </p>
                </div>
              </div>
            ))
          ) : (
            <p className="text-gray-500">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô...</p>
          )}
        </div>

        {/* ‡∏õ‡∏∏‡πà‡∏°‡∏•‡∏π‡∏Å‡∏®‡∏£‡∏Ç‡∏ß‡∏≤ */}
        <button
          onClick={() => scroll("right")}
          className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-3 rounded-full shadow-lg hover:bg-gray-700 z-10"
        >
          <ChevronRight size={24} />
        </button>
      </div>
    </section>
  );
}

function Home() {
  return (
    <div className="w-full">
      {/* Hero Section */}
