import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditExercise() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [exercise, setExercise] = useState({
    question: "",
    option_a: "",
    option_b: "",
    option_c: "",
    option_d: "",
    correct_answer: "",
    image_file: null,
    image_preview: null,
  });

  // โหลดข้อมูล exercise จาก backend
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setExercise({
          question: data.question || "",
          option_a: data.option_a || "",
          option_b: data.option_b || "",
          option_c: data.option_c || "",
          option_d: data.option_d || "",
          correct_answer: data.correct_answer || "",
          image_file: null,
          image_preview: data.image_data
            ? `data:image/jpeg;base64,${data.image_data}`
            : null,
        });
      })
      .catch((err) => console.error("Error fetching exercise:", err));
  }, [id]);

  // handle input & image change
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files && files.length > 0) {
      const file = files[0];
      setExercise((prev) => ({
        ...prev,
        image_file: file,
        image_preview: URL.createObjectURL(file),
      }));
    } else {
      setExercise((prev) => ({ ...prev, [name]: value }));
    }
  };

  // handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    const confirm = await MySwal.fire({
      title: "ยืนยันการบันทึกการแก้ไข?",
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก",
    });
    if (!confirm.isConfirmed) return;

    try {
      const formData = new FormData();
      formData.append("question", exercise.question);
      formData.append("option_a", exercise.option_a);
      formData.append("option_b", exercise.option_b);
      formData.append("option_c", exercise.option_c);
      formData.append("option_d", exercise.option_d);
      formData.append("correct_answer", exercise.correct_answer);
      if (exercise.image_file) {
        formData.append("image", exercise.image_file);
      }

      const res = await fetch(`http://localhost:3000/exercises/${id}`, {
        method: "PUT",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      await MySwal.fire("สำเร็จ!", "แก้ไขคำถามเรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      console.error("Error updating exercise:", err);
      MySwal.fire("ผิดพลาด", `เกิดข้อผิดพลาด: ${err.message}`, "error");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำถาม</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="question"
          value={exercise.question}
          onChange={handleChange}
          placeholder="คำถาม"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="option_a"
          value={exercise.option_a}
          onChange={handleChange}
          placeholder="ตัวเลือก A"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="option_b"
          value={exercise.option_b}
          onChange={handleChange}
          placeholder="ตัวเลือก B"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="option_c"
          value={exercise.option_c}
          onChange={handleChange}
          placeholder="ตัวเลือก C"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="option_d"
          value={exercise.option_d}
          onChange={handleChange}
          placeholder="ตัวเลือก D"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="correct_answer"
          value={exercise.correct_answer}
          onChange={handleChange}
          placeholder="คำตอบที่ถูกต้อง (เช่น A, B, C, D)"
          className="w-full border px-3 py-2 rounded-md"
        />

        <input type="file" name="image" onChange={handleChange} />
        {exercise.image_preview && (
          <img
            src={exercise.image_preview}
            alt="Exercise"
            className="mt-2 w-32 h-32 object-cover rounded-md"
          />
        )}

        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded-md"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
