const Hero = () => {
  return (
    <section className="relative bg-gray-200">
      <img
        src="/hero.jpg"
        alt="Thai Sign"
        className="w-full h-[400px] object-cover"
      />
      <div className="absolute inset-0 flex flex-col items-center justify-center text-center text-white bg-black/40">
        <h1 className="text-3xl md:text-5xl font-bold mb-4">
          การเรียนรู้ภาษามือไทยเบื้องต้นสำหรับผู้เริ่มต้น
        </h1>
        <p className="mb-6 max-w-2xl">
          ภาษามือที่ใช้ท่าทางมือ นิ้วมือ การเคลื่อนไหว สีหน้า และท่าทางร่างกาย...
        </p>
        <button className="bg-blue-600 px-6 py-3 rounded-lg hover:bg-blue-700">
          Start Learning Now
        </button>
      </div>
    </section>
  );
};

export default Hero;
