import { useEffect, useState } from "react";

function Exercise() {
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/exercises") // ต้องตรง port backend
      .then((res) => res.json())
      .then((data) => setExercises(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="px-6 py-12">
      <h1 className="text-3xl font-bold">แบบฝึกหัด</h1>
      <p className="text-gray-600 mt-2">ทั้งหมด {exercises.length} บท</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        {exercises.map((lesson, index) => (
          <div
            key={index}
            className="flex items-start bg-gray-100 p-6 rounded-lg shadow-sm hover:shadow-md transition cursor-pointer"
            // สามารถเพิ่ม onClick เพื่อไปหน้าแบบฝึกหัดของบทเรียน
            onClick={() => console.log("ไปทำแบบฝึกหัดบท", lesson.lesson_number)}
          >
            {/* Circle Number */}
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full text-lg font-bold">
              {exercise.lesson_number || index + 1}
            </div>

            {/* Lesson Content */}
            <div className="ml-4">
              <h3 className="font-semibold text-lg">
                บทที่ {exercise.lesson_number || index + 1}: {exercise.lesson_title}
              </h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Exercise;
