import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Users, Barcode } from "lucide-react";
import Swal from "sweetalert2"; // ‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏° import
import Card from "../components-Dashboard/Card";
import ContentSection from "../components-Dashboard/ContentSection";
import AdminTable from "../components-Dashboard/AdminTable";
import Header from "../components-Dashboard/Header";
import Sidebar from "../components-Dashboard/Sidebar";


export default function Dashboard() {
  const navigate = useNavigate();
  const [lessons, setLessons] = useState([]);
  const [words, setWords] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [admins, setAdmins] = useState([]);

  useEffect(() => {
    fetchLessons();
    fetchWords();
    fetchQuestions();
    fetchAdmins();
  }, []);

  const fetchLessons = async () => {
    try {
      const res = await fetch("http://localhost:3000/lessons");
      const data = await res.json();
      setLessons(data); // ‡πÄ‡∏Å‡πá‡∏ö object ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
    } catch (err) {
      console.error("Error fetching lessons:", err);
    }
  };

  const fetchWords = async () => {
    try {
      const res = await fetch("http://localhost:3000/signs");
      const data = await res.json();
      setWords(data); // ‡πÄ‡∏Å‡πá‡∏ö object ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
    } catch (err) {
      console.error("Error fetching signs:", err);
    }
  };

  const fetchQuestions = async () => {
    try {
      const res = await fetch("http://localhost:3000/exercises");
      const data = await res.json();
      setQuestions(data); // ‡πÄ‡∏Å‡πá‡∏ö object ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
    } catch (err) {
      console.error("Error fetching exercises:", err);
    }
  };

  const fetchAdmins = async () => {
    try {
      const res = await fetch("http://localhost:3000/admins");
      const data = await res.json();
      setAdmins(data);
    } catch (err) {
      console.error("Error fetching admins:", err);
    }
  };

  const handleAddClick = async () => {
    const result = await Swal.fire({
      title: "‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà",
      text: "‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏Ñ‡∏∏‡∏ì‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏û‡∏¥‡πà‡∏°:",
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "",
      showDenyButton: true,
      showDenyButtonLabel: false,
      html: `
        <div class="flex flex-col space-y-2">
          <button id="add-lesson" class="swal2-confirm swal2-styled w-full bg-blue-500 hover:bg-blue-600">‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô</button>
          <button id="add-sign" class="swal2-confirm swal2-styled w-full bg-green-500 hover:bg-green-600">‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå</button>
          <button id="add-exercise" class="swal2-confirm swal2-styled w-full bg-purple-500 hover:bg-purple-600">‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°</button>
          <button id="add-admin" class="swal2-confirm swal2-styled w-full bg-red-500 hover:bg-red-600">‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•</button>
        </div>
      `,
          showConfirmButton: false, 
    });

    // ‚úÖ ‡πÉ‡∏ä‡πâ event listener ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏õ‡∏∏‡πà‡∏°‡∏Å‡∏±‡∏ö navigate
    document.getElementById("add-lesson")?.addEventListener("click", () => {
      Swal.close();
      navigate("/add-lesson");
    });

    document.getElementById("add-sign")?.addEventListener("click", () => {
      Swal.close();
      navigate("/add-sign");
    });

    document.getElementById("add-exercise")?.addEventListener("click", () => {
      Swal.close();
      navigate("/add-exercise");
    });

    document.getElementById("add-admin")?.addEventListener("click", () => {
      Swal.close();
      navigate("/add-admin");
    });
  };

  // ‡∏™‡πà‡∏ß‡∏ô‡∏Ç‡∏≠‡∏á UI
  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar />

      <div className="flex-1 p-8">
        <Header />
        <h1 className="text-3xl font-bold text-gray-800 mb-8">üìä Dashboard</h1>

        {/* Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <Card title="Total Users" value="120" icon={Users} />
          <Card title="Content Items" value="53" icon={Barcode} />
        </div>

        {/* Content Management */}
        <section>
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Content Management</h2>
          <div className="flex justify-between items-center mb-6">
            <input
              type="text"
              placeholder="Search..."
              className="border border-gray-300 rounded-md px-3 py-2 text-sm w-1/2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              onClick={handleAddClick} // ‚úÖ ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô popup
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              + Add
            </button>
          </div>

          {/* ‡∏™‡πà‡∏ß‡∏ô‡πÄ‡∏î‡∏¥‡∏°‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡πÅ‡∏Å‡πâ */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <ContentSection
              title="‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô"
              items={lessons}
              onEdit={(lesson) => navigate(`/edit-lesson/${lesson.lesson_id}`)}
              onDelete={async (lesson) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, { method: "DELETE" });
                  fetchLessons();
                }
              }}
            />
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå"
              items={words}
              onEdit={(word) => navigate(`/edit-word/${word.word_id}`)}
              onDelete={async (word) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/signs/${word.word_id}`, { method: "DELETE" });
                  fetchWords();
                }
              }}
            />
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°"
              items={questions}
              onEdit={(q) => navigate(`/edit-exercise/${q.question_id}`)}
              onDelete={async (q) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/exercises/${q.question_id}`, { method: "DELETE" });
                  fetchQuestions();
                }
              }}
            />
          </div>
        </section>

        {/* Admin Table */}
        <section className="mt-10">
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Admin Management</h2>
          <AdminTable data={admins} />
        </section>
      </div>
    </div>
  );
}
