// src/pages/Exercise.jsx
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Navbar from "../components/Narbar"; // import ถูกต้อง

function Exercise() {
  const [lessons, setLessons] = useState([]);
  const navigate = useNavigate();
  const { user, token } = useAuth(); // ใช้ตัวเดียวกับ Navbar

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  const handleLessonClick = (lessonId) => {
    if (!user || !token) {
      // เด้งไป login พร้อมส่ง path ที่ต้องการไปหลัง login
      navigate("/login", { state: { redirectTo: `/exercises/lesson/${lessonId}` } });
    } else {
      navigate(`/exercises/lesson/${lessonId}`);
    }
  };

}

export default Exercise;
