import { useEffect, useState } from "react";

function App() {
  const [welcome, setWelcome] = useState("");
  const [lessons, setLessons] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [signs, setSigns] = useState([]);

  // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏° Welcome
  useEffect(() => {
    fetch("http://localhost:5000/api/")
      .then((res) => res.json())
      .then((data) => setWelcome(data.message))
      .catch((err) => console.error("Error:", err));
  }, []);

  // ‡∏î‡∏∂‡∏á Lessons
  useEffect(() => {
    fetch("http://localhost:5000/api/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  // ‡∏î‡∏∂‡∏á Exercises
  useEffect(() => {
    fetch("http://localhost:5000/api/exercises")
      .then((res) => res.json())
      .then((data) => setExercises(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  // ‡∏î‡∏∂‡∏á Signs
  useEffect(() => {
    fetch("http://localhost:5000/api/signs")
      .then((res) => res.json())
      .then((data) => setSigns(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">{welcome}</h1>

      <section className="mb-6">
        <h2 className="text-xl font-semibold">üìò Lessons</h2>
        <ul className="list-disc pl-5">
          {lessons.map((lesson, i) => (
            <li key={i}>{JSON.stringify(lesson)}</li>
          ))}
        </ul>
      </section>

      <section className="mb-6">
        <h2 className="text-xl font-semibold">üèãÔ∏è Exercises</h2>
        <ul className="list-disc pl-5">
          {exercises.map((ex, i) => (
            <li key={i}>{JSON.stringify(ex)}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2 className="text-xl font-semibold">‚úã Signs</h2>
        <ul className="list-disc pl-5">
          {signs.map((sign, i) => (
            <li key={i}>{JSON.stringify(sign)}</li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default App;
