import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // üëà ‡πÄ‡∏û‡∏¥‡πà‡∏° useNavigate

function Testexercise() {
  const { id } = useParams();
  const navigate = useNavigate(); // üëà ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô hook ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö
  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${id}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [id]);

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const questions = lesson.questions;

  const handleNext = () => {
    setUserAnswers([...userAnswers, selectedOption]);

    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }

    setSelectedOption("");

    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowResult(true);
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
  };

  // ‚úÖ ‡∏´‡∏ô‡πâ‡∏≤‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
  if (showResult) {
    return (
      <div className="px-6 py-12 flex justify-center">
        <div className="container max-w-3xl text-center">
          <h1 className="text-3xl font-bold">‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì</h1>
          <p className="mt-4 text-lg">
            ‡∏Ñ‡∏∏‡∏ì‡∏ó‡∏≥‡∏ñ‡∏π‡∏Å {score} / {questions.length} ‡∏Ç‡πâ‡∏≠
          </p>

          <div className="mt-6 flex flex-wrap justify-center gap-4">
            <button
              className="px-4 py-2 bg-green-500 text-white rounded"
              onClick={handleRestart}
            >
              ‡∏ó‡∏≥‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á
            </button>
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded"
              onClick={() => setShowAnswers(true)}
            >
              ‡πÄ‡∏ä‡πá‡∏Ñ‡∏Ç‡πâ‡∏≠‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å/‡∏ú‡∏¥‡∏î
            </button>
            {/* üëá ‡∏õ‡∏∏‡πà‡∏°‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö */}
            <button
              className="px-4 py-2 bg-gray-500 text-white rounded"
              onClick={() => navigate(-1)} // üëà ‡∏Å‡∏•‡∏±‡∏ö‡∏´‡∏ô‡πâ‡∏≤‡∏Å‡πà‡∏≠‡∏ô‡∏´‡∏ô‡πâ‡∏≤
            >
              ‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö
            </button>
          </div>

          {showAnswers && (
            <div className="mt-8 text-left">
              <h2 className="text-2xl font-semibold mb-4 text-center">
                ‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏ó‡∏≥‡∏Ç‡πâ‡∏≠‡∏™‡∏≠‡∏ö
              </h2>
              {questions.map((q, idx) => {
                const isCorrect = userAnswers[idx] === q.answer;
                return (
                  <div
                    key={idx}
                    className={`p-4 mb-3 rounded border ${
                      isCorrect
                        ? "bg-green-100 border-green-500"
                        : "bg-red-100 border-red-500"
                    }`}
                  >
                    <p className="font-semibold">
                      {idx + 1}. {q.question}
                    </p>
                    <p className="mt-2">
                      ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì:{" "}
                      <span
                        className={isCorrect ? "text-green-700" : "text-red-700"}
                      >
                        {userAnswers[idx]}
                      </span>
                    </p>
                    {!isCorrect && (
                      <p className="mt-1 text-green-700">
                        ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á: {q.answer}
                      </p>
                    )}
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ‚úÖ ‡∏´‡∏ô‡πâ‡∏≤‡∏ó‡∏≥‡∏Ç‡πâ‡∏≠‡∏™‡∏≠‡∏ö
  const question = questions[currentQuestion];

  return (
    <div className="px-6 py-12 flex justify-center">
      <div className="container max-w-3xl text-center">
        <h1 className="text-3xl font-bold">{lesson.title}</h1>
        <p className="mt-2 text-gray-600">
          ‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏° {currentQuestion + 1} / {questions.length}
        </p>

        <div className="mt-6 bg-gray-100 p-6 rounded-lg shadow text-left">
          <h3 className="text-lg font-semibold">{question.question}</h3>

          {question.image_data && (
            <div className="flex justify-center mt-4">
              <img
                src={`data:image/jpeg;base64,${question.image_data}`}
                alt="question"
                className="w-40 h-40 object-contain rounded"
              />
            </div>
          )}

          <div className="mt-4 flex flex-col gap-3">
            {question.options.map((opt, idx) => (
              <button
                key={idx}
                className={`p-3 rounded border text-left transition
                ${
                  selectedOption === opt
                    ? "bg-blue-500 text-white"
                    : "bg-white hover:bg-blue-50"
                }`}
                onClick={() => setSelectedOption(opt)}
              >
                {opt}
              </button>
            ))}
          </div>

          <div className="flex justify-center gap-4">
  {/* ‡∏õ‡∏∏‡πà‡∏°‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö */}
  <button
    className="mt-6 px-6 py-2 bg-gray-400 text-white rounded disabled:opacity-50"
    onClick={() => {
      if (currentQuestion > 0) {
        setCurrentQuestion(currentQuestion - 1);
        // ‡∏î‡∏∂‡∏á‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡πÄ‡∏î‡∏¥‡∏°‡∏Å‡∏•‡∏±‡∏ö‡∏°‡∏≤‡πÇ‡∏ä‡∏ß‡πå ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ
        setSelectedOption(userAnswers[currentQuestion - 1] || "");
      }
    }}
    disabled={currentQuestion === 0}
  >
    ‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö
  </button>

  {/* ‡∏õ‡∏∏‡πà‡∏°‡∏ñ‡∏±‡∏î‡πÑ‡∏õ‡∏´‡∏£‡∏∑‡∏≠‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏• */}
  <button
    className="mt-6 px-6 py-2 bg-green-500 text-white rounded disabled:opacity-50"
    onClick={handleNext}
    disabled={!selectedOption}
  >
    {currentQuestion + 1 < questions.length ? "‡∏ñ‡∏±‡∏î‡πÑ‡∏õ" : "‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•"}
  </button>
</div>

        </div>
      </div>
    </div>
  );
}

export default Testexercise;
