import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function AddSign() {
  const navigate = useNavigate();
  const [lessonId, setLessonId] = useState("");
  const [word, setWord] = useState("");
  const [meaning, setMeaning] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [videoUrl, setVideoUrl] = useState("");
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    // ดึง list lesson จาก backend เพื่อเลือก
    fetch(`http://localhost:3000/signs/${id}`)
      .then(res => res.json())
      .then(data => setLessons(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("lesson_id", lessonId);
    formData.append("word", word);
    formData.append("meaning", meaning);
    formData.append("description", description);
    formData.append("video_url", videoUrl);
    if (image) formData.append("image_data", image);

    try {
      const res = await fetch("http://localhost:3000/signs//${id}", {
        method: "POST",
        body: formData,
      });
      if (res.ok) {
        alert("Sign added successfully");
        navigate("/dashboard");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to add sign");
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Add Sign</h1>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-md">
        <select value={lessonId} onChange={(e) => setLessonId(e.target.value)} required>
          <option value="">Select Lesson</option>
          {lessons.map(l => (
            <option key={l.lesson_id} value={l.lesson_id}>{l.lesson_title}</option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Word"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Meaning"
          value={meaning}
          onChange={(e) => setMeaning(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />
        <input
          type="text"
          placeholder="Video URL"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Save
        </button>
      </form>
    </div>
  );
}
