import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);

  // ⏱️ เวลาแบบถอยหลัง (90 วินาที)
  const [timeLeft, setTimeLeft] = useState(90);

  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${id}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [id]);

  // ⏱️ เริ่มจับเวลาถอยหลัง
  useEffect(() => {
    if (showResult) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleTimeUp(); // หมดเวลา → สรุปผลทันที
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [showResult]);

  // ⏱️ แปลงเวลาเป็น นาที:วินาที
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const questions = lesson.questions;

  const handleNext = () => {
    setUserAnswers([...userAnswers, selectedOption]);

    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }

    setSelectedOption("");

    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowResult(true);
    }
  };

  // ✅ ฟังก์ชันเมื่อหมดเวลา
  const handleTimeUp = () => {
    // บันทึกคำตอบของข้อปัจจุบันถ้ามีเลือก
    if (selectedOption) {
      setUserAnswers((prev) => [...prev, selectedOption]);
      if (selectedOption === questions[currentQuestion].answer) {
        setScore((prev) => prev + 1);
      }
    }
    setShowResult(true); // สรุปผลอัตโนมัติ
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
  };

  // ✅ หน้าผลลัพธ์
  if (showResult) {
    return (
      <div className="px-6 py-12 flex justify-center">
        <div className="container max-w-3xl text-center">
          <h1 className="text-3xl font-bold">ผลลัพธ์ของคุณ</h1>
          <p className="mt-4 text-lg">
            คุณทำถูก {score} / {questions.length} ข้อ
          </p>
          <p className="mt-2 text-gray-700">
            เวลาที่ใช้: {formatTime(90 - timeLeft)}
          </p>

          <div className="mt-6 flex flex-wrap justify-center gap-4">
            <button
              className="px-4 py-2 bg-green-500 text-white rounded"
              onClick={handleRestart}
            >
              ทำอีกครั้ง
            </button>
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded"
              onClick={() => setShowAnswers(true)}
            >
              เช็คข้อที่ถูก/ผิด
            </button>
            <button
              className="px-4 py-2 bg-gray-500 text-white rounded"
              onClick={() => navigate(-1)}
            >
              ย้อนกลับ
            </button>
          </div>

          {showAnswers && (
            <div className="mt-8 text-left">
              <h2 className="text-2xl font-semibold mb-4 text-center">
                ผลการทำข้อสอบ
              </h2>
              {questions.map((q, idx) => {
                const isCorrect = userAnswers[idx] === q.answer;
                return (
                  <div
                    key={idx}
                    className={`p-4 mb-3 rounded border ${
                      isCorrect
                        ? "bg-green-100 border-green-500"
                        : "bg-red-100 border-red-500"
                    }`}
                  >
                    <p className="font-semibold">
                      {idx + 1}. {q.question}
                    </p>
                    <p className="mt-2">
                      คำตอบของคุณ:{" "}
                      <span
                        className={
                          isCorrect ? "text-green-700" : "text-red-700"
                        }
                      >
                        {userAnswers[idx]}
                      </span>
                    </p>
                    {!isCorrect && (
                      <p className="mt-1 text-green-700">
                        คำตอบที่ถูกต้อง: {q.answer}
                      </p>
                    )}
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ✅ หน้าทำข้อสอบ
  const question = questions[currentQuestion];

  return (
    <div className="px-6 py-12 flex justify-center">
      <div className="container max-w-3xl text-center">
        <h1 className="text-3xl font-bold">{lesson.title}</h1>
        <p className="mt-2 text-gray-600">
          คำถาม {currentQuestion + 1} / {questions.length}
        </p>
        <p className={`mt-1 text-xl font-semibold ${timeLeft <= 10 ? "text-red-500" : "text-green-700"}`}>
          เวลาเหลือ: {formatTime(timeLeft)}
        </p>
        {timeLeft === 0 && (
          <p className="mt-2 text-red-600 font-bold">หมดเวลาแล้ว ❌</p>
        )}

        <div className="mt-6 bg-gray-100 p-6 rounded-lg shadow text-left">
          <h3 className="text-lg font-semibold">{question.question}</h3>

          {question.image_data && (
            <div className="flex justify-center mt-4">
              <img
                src={`data:image/jpeg;base64,${question.image_data}`}
                alt="question"
                className="w-40 h-40 object-contain rounded"
              />
            </div>
          )}

          <div className="mt-4 flex flex-col gap-3">
            {question.options.map((opt, idx) => (
              <button
                key={idx}
                className={`p-3 rounded border text-left transition ${
                  selectedOption === opt
                    ? "bg-blue-500 text-white"
                    : "bg-white hover:bg-blue-50"
                } ${timeLeft === 0 ? "opacity-50 cursor-not-allowed" : ""}`}
                onClick={() => timeLeft > 0 && setSelectedOption(opt)}
                disabled={timeLeft === 0}
              >
                {opt}
              </button>
            ))}
          </div>

          <div className="flex justify-center gap-4 mt-6">
            <button
              className="px-6 py-2 bg-gray-400 text-white rounded disabled:opacity-50"
              onClick={() => {
                if (currentQuestion > 0) {
                  setCurrentQuestion(currentQuestion - 1);
                  setSelectedOption(userAnswers[currentQuestion - 1] || "");
                }
              }}
              disabled={currentQuestion === 0 || timeLeft === 0}
            >
              ย้อนกลับ
            </button>

            <button
              className="px-6 py-2 bg-green-500 text-white rounded disabled:opacity-50"
              onClick={handleNext}
              disabled={!selectedOption || timeLeft === 0}
            >
              {currentQuestion + 1 < questions.length ? "ถัดไป" : "สรุปผล"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Testexercise;
