import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditSign() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [sign, setSign] = useState({
    word: "",
    meaning: "",
    video_url: "",
    lesson_id: "",
    image_file: null,
    image_preview: null
  });

  useEffect(() => {
    fetch(`http://localhost:3000/signs/${id}`)
      .then(res => res.json())
      .then(data => {
        setSign({
          ...data,
          image_file: null,
          image_preview: data.image_data || null
        });
      })
      .catch(err => console.error("Error fetching sign:", err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files.length > 0) {
      setSign(prev => ({
        ...prev,
        image_file: files[0],
        image_preview: URL.createObjectURL(files[0])
      }));
    } else {
      setSign(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await MySwal.fire({
      title: "ยืนยันการแก้ไข?",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      const formData = new FormData();
      formData.append("word", sign.word);
      formData.append("meaning", sign.meaning);
      formData.append("video_url", sign.video_url);
      formData.append("lesson_id", sign.lesson_id);
      if (sign.image_file) formData.append("image", sign.image_file);

      const res = await fetch(`http://localhost:3000/signs/${id}`, {
        method: "PUT",
        body: formData
      });

      if (!res.ok) throw new Error("Failed to update sign");
      MySwal.fire("สำเร็จ", "แก้ไขคำศัพท์เรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      MySwal.fire("ผิดพลาด", err.message, "error");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำศัพท์</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input name="word" value={sign.word} onChange={handleChange} placeholder="Word" className="w-full border px-3 py-2 rounded-md" />
        <input name="meaning" value={sign.meaning} onChange={handleChange} placeholder="Meaning" className="w-full border px-3 py-2 rounded-md" />
        <input name="video_url" value={sign.video_url} onChange={handleChange} placeholder="Video URL" className="w-full border px-3 py-2 rounded-md" />
        <input name="lesson_id" value={sign.lesson_id} onChange={handleChange} placeholder="Lesson ID" className="w-full border px-3 py-2 rounded-md" />
        <input type="file" name="image" onChange={handleChange} />
        {sign.image_preview && <img src={sign.image_preview} alt="preview" className="w-32 h-32 object-cover" />}
        <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">บันทึก</button>
      </form>
    </div>
  );
}
